import React, { useRef } from 'react'
import './style.css'

function Task({props,dlt}){
    const ref=useRef('')
    const ChC=()=>{
        
        ref.current.classList.add('rsi')
    }

    return(
       <div>
        <input type="text" name="" id="" value={props} ref={ref}/>
        <input type="button" onClick={dlt} value="Supprimer" className='btn-danger'/>
        <input type="button" value="Done" onClick={ChC} className='btn-info'/>
       </div>
    )
}
export default Task
