import React, { useState } from "react";

function App(){
  const [n1,setn1]=useState()
  const [n2,setn2]=useState()
  const [n3,setn3]=useState()

  const mod1=(e)=>{
    setn1(e.target.value)
  }
  const mod2=(e)=>{
    setn2(e.target.value)
  }
  const mod3=(e)=>{
    setn3(e.target.value)
  }
  const somme=()=>{
    setn3(Number(n1)+Number(n2))
  }

  return(
    <div>
       Number 1: <input type="text" name="" id="" value={mod1}/><br/>
       Number 2: <input type="text" name="" id="" value={mod2}/><br/>
       <input type="button" name="" id=""  value={somme}/><br/>
       Resultat: <input type="text" name="" id="" value={mod1}/>
    </div>
  );
}
export default App
