import React, { useRef } from "react";

function Comp2(){
   const reft=useRef([])
   const checkall=()=>{
      reft.current.checked=true;
   }
   const Uncheckall=()=>{
    reft.current.checked=false;
 }
   
  return(
   <div>
       <h1>Exemple de CheckBox :</h1>
       <input type="checkbox" name="" id="" ref={reft} value="Sport"/>Sport <br />
       <input type="checkbox" name="" id="" ref={reft} value="Voyage"/>Voyage <br />
       <input type="checkbox" name="" id="" ref={reft} value="Music"/>Music

       <input type="button" onClick={checkall} value="CheckAll" />      
       <input type="button" onClick={Uncheckall} value="unCheckAll" />
       <input type="button" value="selectedCheckbox" />
   </div>
  );
}
export default Comp2
