import React, { useRef } from 'react'
import './style.css'

function Task({props,dlt}){
    const ref=useRef('')   
    const done=()=>{
       let v
      if(v=true){
        v=true
        v=ref.current.classList.add('rsi')
        
        console.log(v)
        
      }else if(v=false){
       
        ref.current.classList.remove('rsi')
       
        console.log(v)
        
       
  
      }
    
    }  
  
    return(
       <div>
        <input type="text" name="" id="inp" value={props} ref={ref}/>
        <input type="button" onClick={dlt} value="Supprimer" className='btn-danger'/>
        <input type="button" value="Done" id="do" onClick={done} className='btn-info'/>
       </div>
    )
}
export default Task
