import React, { useState } from 'react'
import Task from './Task'
import {v4 as uuidv4} from 'uuid'

function Todo(){
    const [tasks,settasks]=useState([
        {id:uuidv4(),Job:'Sport'},
        {id:uuidv4(),Job:'Lire'},
        {id:uuidv4(),Job:'Coder'}
    ])
    const[task,settask]=useState('')

    const addtask=()=>{
        let ntasks=[...tasks]
        let ntask={}
        ntask.id=uuidv4()
        ntask.Job=task
        ntasks.push(ntask)
        settasks(ntasks)
        
    }

    return(
       <div>
        <h1>ToDo - List</h1>
        <form action="">
            Next Job : <input type="text" onChange={settask} name="" id="" value={task}/> <input type="button" onClick={addtask} value="Ajouter" className='btn-primary'/>
        </form>
        <ul>
            {
            tasks.map((t)=>{
                return <li key={t.id}><Task props={t.Job}/></li>
            })
        }
        </ul>
       </div>
    )
}
export default Todo
